import{_ as i,c,o as a,ae as t}from"./chunks/framework.CBTkueSR.js";const n=JSON.parse('{"title":"技术组 | YUI动漫社","description":"","frontmatter":{"title":"技术组 | YUI动漫社"},"headers":[],"relativePath":"zucheng/jishu.md","filePath":"zucheng/jishu.md"}'),e={name:"zucheng/jishu.md"};function d(l,s,o,v,h,p){return a(),c("div",null,[...s[0]||(s[0]=[t('<div class="tech-group-container"><div class="group-header"><h1 class="group-name">技术组</h1><p class="group-motto">宗旨：烙饼王，我当定了</p></div><div class="section"><h2 class="section-title">关于技术组</h2><p>技术组是社团的&quot;幕后工程师团队&quot;，负责用数字技术实现创意构想。无论是活动视频剪辑、动漫特效制作，还是社团官网维护，都能看到我们的身影。我们欢迎对技术有热情的同学加入，无论是否有基础，只要愿意学习就能共同进步。</p><p>我们的核心目标是：降低技术门槛，让更多人能通过技术表达对二次元的热爱，同时为社团活动提供稳定的技术支持。技术组不仅是技能提升的平台，更是技术爱好者交流分享的社群，在这里可以找到志同道合的伙伴一起攻克技术难题。</p></div><div class="section"><h2 class="section-title">日常活动</h2><div class="activities-list"><div class="activity-item"><h3 class="activity-title"><i>🎬</i> 视频制作工坊 </h3><p class="activity-desc">教授Pr、AE，PS等软件使用，涵盖动漫混剪、活动记录片制作、MMD动画渲染等内容。</p></div><div class="activity-item"><h3 class="activity-title"><i>💻</i> 技术分享会 </h3><p class="activity-desc">每月邀请组内大佬，分享图片后期（PS）、建模、网站搭建等技能，零基础也能快速上手。</p></div><div class="activity-item"><h3 class="activity-title"><i>📱</i> 项目协作 </h3><p class="activity-desc">承接社团各类技术需求，如mmd制作、虚拟皮套开发、图片动效与特效制作、海报设计排版等，在实战中提升技能，积累真实项目经验。</p></div><div class="activity-item"><h3 class="activity-title"><i>🎮</i> 创意技术探索 </h3><p class="activity-desc">尝试二次元与新兴技术结合，如AI辅助视图观测、虚拟主播模型搭建，探索技术在二次元领域的更多可能。</p></div></div></div><div class="section"><h2 class="section-title">作品展示</h2><div class="showcase"><div class="showcase-grid"><div class="showcase-item"><div class="showcase-img"><img src="https://pic.nwafu.xyz/images/639785b2839929c519e8fbfd0ad1554ac4242cc107d8b4876f3006f73ee15ace/1762252223601-komiayht-img_mhkfgwox_q9zwvo.png" alt="特效制作"></div><div class="showcase-info"><h3 class="showcase-title">特效制作</h3><p class="showcase-desc">使用ps，AE制作特效，加入粒子动画，使出片更加炫狂。</p></div></div><div class="showcase-item"><div class="showcase-img"><img src="https://pic.nwafu.xyz/images/639785b2839929c519e8fbfd0ad1554ac4242cc107d8b4876f3006f73ee15ace/1761807445025-r2f2z0ez-img_mhd2nmam_dyncob.png" alt="社团官网改版设计"></div><div class="showcase-info"><h3 class="showcase-title">社团官网改版设计</h3><p class="showcase-desc">基于Vue开发的响应式网站，包含活动日历、成员作品库、在线报名系统等功能模块，适配手机和电脑端。</p></div></div><div class="showcase-item"><div class="showcase-img"><img src="https://pic.nwafu.xyz/images/639785b2839929c519e8fbfd0ad1554ac4242cc107d8b4876f3006f73ee15ace/1761807508359-otkma1of-img_mhd2p9dx_95yo1e.jpg" alt="动漫角色3D建模练习"></div><div class="showcase-info"><h3 class="showcase-title">动漫角色3D建模练习</h3><p class="showcase-desc">制作社团形象3D模型，包含骨骼绑定和基础动作，可用于3D动漫这座，打印周边或VR场景展示。</p></div></div></div></div></div></div>',1)])])}const r=i(e,[["render",d]]);export{n as __pageData,r as default};
